// colors
$forrest: #212620;
$orange: #FF5900;
$creme: #FCF5CF;

// font family
$font-main: 'Libre Franklin', sans-serif;
$font-accent: 'Sedgwick Ave', cursive;
//$font-accent: 'Shadows Into Light', cursive;

// font-weights
$thin: 100;
$regular: 400;
$bold: 700;
$extra-bold: 800;

