@import url("../../node_modules/@fortawesome/fontawesome-free/css/fontawesome.min.css");
@import '../scss/base.scss';
@import '../scss/mixins.scss';
@import '../scss/variables.scss';


// General Styling
body {
    background: $forrest;
    font-family: $font-main;
    color: $creme;
}

.btn-cta {
    @include ctaBtnOrange;
    display: flex;
    align-items: center;
    gap: 1em;
    font-weight: $bold;
    transition-duration: 200ms;

    &:hover {
        color: #ff7c35;
        transform: scale(2rem)
    }

    &.uppercase {
        @include uppercase;
    }

}




//// Typography
.logo {
    @include accentText;
    width: min-content;
    margin: 0;
    font-size: $logo;
}

// .nav-list--item {
//     font-size: clamp(1rem, 1.849vw, 2.125rem  ); //18px, 19.2px, 34

//     //font-size: clamp(.75rem, 1.541vw, 1.5rem  ); //18px, 19.2px, 34
// }

// .text-small {
//     @include smallText;
// }

.title {
    @include accentText;
    font-size: clamp(2.5rem, 4.5vw, 6rem); // ideal -->  2.875rem/46px/4.5vw
    margin: 1.5em 0 0 0;
    line-height: .55;

}

.title-secondary {
    @include uppercase;
    margin: 0;
    padding: 0;
    font-size: clamp(5rem, 8.5vw, 10rem);
    font-weight: $extra-bold;
    text-transform: uppercase;
}

.subtitle-cursive {
    @include accentText;
}

.subtitle-accent {
    @include ctaBtnOrange;
    font-weight: $bold;
}

.subtitle-thin {
    @include ctaBtnOrange;
    font-weight: $thin;
}

.subtitle-small {
    font-weight: $thin;
}


//// Cards

.card {
    background: #333B31;
    width: 340px; //actual 325px minofcol350px
    height: 520px;
    overflow: hidden;
    position: relative;
    //border-radius: 10px;

    &:hover {
        box-shadow: 15px 15px 25px 5px rgba(0, 0, 0, 0.25); 
    }

    & .card-img  {
        width: 100%;
        height: 100%;
        position: static;
        background-position: center;
        background-size: cover;
        clip-path: polygon(0 0, 100% 0, 100% 40%, 0 100%); 

        &.ani {
            background-image: url('../images/ani.jpg');
        }
    
        &.andre {
            background-image: url('../images/andre.jpg');
        }
    
        &.mica {
            background-image: url('../images/mica.jpg');
        }
    }

    & .card-body {
        position: absolute;
        bottom: 25px;
        right: 0px;
        padding-right: .75em;
        display: flex;
        flex-direction: column;
        align-items: flex-end;
    }

    .card-title {
        @include accentText;
        margin: 0;
        font-size: 2.55rem;
    }

    .subtitle-small {
        font-size: 1rem;
        margin-bottom: 1.25em;
    }

    .btn-cta {
        font-size: 1.1rem;
    }

}


//// Card Stack
.stack {
    //border: solid purple 2px;
    margin: 2.5em auto;
    padding: 1em;
    max-width: 1200px;
    min-height: 20vh;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(315px, 1fr));
    justify-items: center;
    gap: 2em;
}




//// Header
header {
    //border: solid red 2px;
    padding: 2em 1em;
    display: flex;
    flex-direction: column;
    gap: 2.5em;
    
    & .logo {
        text-align: center;
    }

    & .nav-list, 
    .social-links {
        //border: solid yellow 2px;
        display: flex;
        justify-content: center;
        gap: 2.75em;
    }

    .social-link {
        font-size: 1.75rem;
    }

    & li:nth-child(1) {order: 2;}
    & li:nth-child(2) {order: 1; align-self: center;}
    & li:nth-child(3) {order: 3; align-self: center;}
}




//// Main
main {
    //border: solid red 2px;
    display: flex;
    flex-direction: column;
    gap: 10rem;
    padding: 3.5em 0 4em 0;
    background: linear-gradient(90deg, rgba(33,38,32,1) 0%, rgba(33,38,32,1) 15%, rgba(31,36,30,1) 15%,
    rgba(31,36,30,1) 20%, rgba(33,38,32,1) 20%, rgba(33,38,32,1) 22%, rgba(31,36,30,1) 22%,
    rgba(31,36,30,1) 27%, rgba(33,38,32,1) 27%, rgba(33,38,32,1) 29%, rgba(31,36,30,1) 29%,
    rgba(31,36,30,1) 34%, rgba(33,38,32,1) 34%, rgba(33,38,32,1) 36%, rgba(31,36,30,1) 36%,
    rgba(31,36,30,1) 41%, rgba(33,38,32,1) 41%, rgba(33,38,32,1) 43%, rgba(31,36,30,1) 43%,
    rgba(31,36,30,1) 48%, rgba(33,38,32,1) 48%, rgba(33,38,32,1) 50%, rgba(31,36,30,1) 50%,
    rgba(31,36,30,1) 55%, rgba(33,38,32,1) 55%, rgba(33,38,32,1) 57%, rgba(31,36,30,1) 57%,
    rgba(31,36,30,1) 62%, rgba(33,38,32,1) 62%, rgba(33,38,32,1) 64%, rgba(31,36,30,1) 64%,
    rgba(31,36,30,1) 69%, rgba(33,38,32,1) 69%, rgba(33,38,32,1) 71%, rgba(31,36,30,1) 71%,
    rgba(31,36,30,1) 76%, rgba(33,38,32,1) 76%, rgba(33,38,32,1) 78%, rgba(31,36,30,1) 78%,
    rgba(31,36,30,1) 83%, rgba(33,38,32,1) 83%);

    // & section {
    //     border: solid 2px purple;
    // }

    & .hero {

        & .hero-img {
            box-shadow: $img-shadow;
        }

        & .subtitle-group {
            margin-top: 1em;;
            max-width: 700px;
            display: flex;
            justify-content: space-between;

            & :nth-child(2) {
                display: flex;
                align-items: flex-end;
            }
        }

        & .medium {
            font-size: 1.75rem;
        }
    }

    & .cta {
        margin-top: 1.5em;

        & .cta-img {
            clip-path: polygon(0 0, 100% 0, 50% 100%, 0 100%);
        }

        & .cta-body {
            border-left: solid 10px #0000005e;
            width: 600px;
            margin: 2em 0 0 0;
            //clip-path: polygon(0 0, 50% 0, 100% 100%, 0 100%);

            .wrapper {
                width: 60%;
                padding: 1em;
                display: flex;
                flex-direction: column;
                align-items: flex-start;

                & > h2 {
                    margin: 0 ;
                }

                & > p {
                    line-height: 1.4;
                }
                
                & > * {
                    &:not(:first-child) {
                        margin-top: 1.5em 
                    }
                }
            } 
        }

    }

    & .quote {
        //border: solid 2px purple;
        margin-top: 1.5em;
        display: flex;
        flex-direction: column;

        & .quote-img {
            box-shadow: $img-shadow;
        }
        
        & > h2 {
            margin: 0;
            text-align: center;
            font-size: 3.75rem
        }

        & .quote-body {
            display: flex;
            flex-direction: column;
            align-items: flex-end;

            & > h2 {
                margin: 1rem 0 0 0 ;
                font-weight: $regular;
            }

            & > p {
                margin: .25em 0 1.75em 0 ;
            }
        }
    }

    & .sessions {
        //border: solid red 2px;

        & .sessions-body{
            text-align: center;
            max-width: 1200px;
            margin: 0 auto;

            // & > p {
            //     font-size: clamp(rem, 2.93vw, 2rem);
            // }
        }

        & .btn-cta.uppercase {
            width: max-content; //makes the box the width of the content inside
            margin: 4.5em auto;
            text-align: left;
        }


    }
}



//// Footer
footer {
    //border: solid red 2px;
    display: flex;
    width: 100%;
    min-height: 500px;
    margin-top: 5em;
    padding-top: 5.5em;
    padding-bottom: 5.5em;
    background-image: linear-gradient(86deg, #212620 25%, transparent), url(https://i.postimg.cc/N08VVdSB/IMG-7744-Edit.jpg);
